    public function run()
    {
        $faker = Faker::create();

        $trainers = [];

        // Mengisi data untuk 50 trainer
        for ($i = 0; $i < 96; $i++) {
            $trainers[] = [
                'name' => $faker->unique()->name,
                'phone' => $faker->unique()->phoneNumber,
                'biography' => $faker->paragraph, // Biografi yang dapat mencakup pengalaman dan keahlian
                'experience' => $faker->numberBetween(1, 30), // Pengalaman dalam tahun
                'password' => bcrypt('password'), // Password default, sebaiknya ganti jika diperlukan
                'created_at' => now(),
                'updated_at' => now(),
            ];
        }

        // Menyisipkan data ke tabel 'trainers'
        DB::table('trainers')->insert($trainers);
    }
